import './App.css';
import Projects from './Projects';
import AboutMe from './AboutMe';
import MazeGame from './MazeGame';
import MySkills from './MySkills'
import Marketplace from './MockTradingPlatform';

import space from './images/space.jpg'
import cool from './images/neatbg.png'
import { Parallax, ParallaxLayer } from '@react-spring/parallax';

import Typewriter from "typewriter-effect";
import React, { useEffect, useRef, useState } from 'react';

function App() {

  const [typewriterVisible, setTypewriterVisible] = useState(false);
  const ref = useRef();

  const handleTypewriterVisibility = (entries) => {
    const [entry] = entries;
    setTypewriterVisible(entry.isIntersecting);
  };

  useEffect(() => {
    const options = {
      root: null,
      rootMargin: '0px',
      threshold: 0.5, 
    };

    const observer = new IntersectionObserver(handleTypewriterVisibility, options);
    if (ref.current) {
      observer.observe(ref.current);
    }


    return () => observer.disconnect();
  }, [ref]);


  return (
    <div>
      <Parallax pages={7} ref={ref}>
        <ParallaxLayer
          offset={0}
          speed={1}
          factor={1}
          onClick={()=> ref.current.scrollTo(1)}
          style={{
            backgroundImage: `url(${space})`,
            backgroundSize: 'cover'
          }}
        ></ParallaxLayer>

        <ParallaxLayer
          offset={1}
          speed={1}
          factor={1}
          onClick={()=> ref.current.scrollTo(2)}
          style={{
            backgroundImage: `url(${cool})`,
            backgroundSize: 'cover'
          }}
        ></ParallaxLayer>

        <ParallaxLayer
          offset={0.2}
          speed={0.05}
          onClick={()=> ref.current.scrollTo(1)}
        >
          <div className="App" ref={ref}>
            {typewriterVisible && (
              <Typewriter
                onInit={(typewriter) => {
                  typewriter.typeString("Hi, I'm Nemanja!").pauseFor(2000).start();
                }}
              />
            )}
          </div>
        </ParallaxLayer>

        <ParallaxLayer
          offset={1.2}
          speed={0.05}
          onClick={()=> ref.current.scrollTo(2)}
        >
          <div className="App">
            <Typewriter
              options={{
                loop: true,
              }}
              onInit={(typewriter) => {
              typewriter.typeString("And this is my website!").pauseFor(2000).start();
            }}/>
          </div>
        </ParallaxLayer>

        <ParallaxLayer
          offset={2}
          speed={1}
          factor={1}
          onClick={()=> ref.current.scrollTo(3)}
        >
          <Projects/>
        </ParallaxLayer>

        <ParallaxLayer
          offset={3}
          speed={1}
          factor={1}
        >
          <MazeGame />
        </ParallaxLayer> 

        <ParallaxLayer
        offset={4}
        speed={1}
        factor={1}
        >
          <Marketplace/>
        </ParallaxLayer>

        <ParallaxLayer
        offset={5}
        speed={1}
        factor={1}
        >
          <MySkills/>
        </ParallaxLayer>

        <ParallaxLayer
        offset={6}
        speed={1}
        factor={1}
        >
          <AboutMe/>
        </ParallaxLayer>
      </Parallax>
    </div>
  );
}

export default App;
