import './App.css';
import Projects from './Projects';
import AboutMe from './AboutMe';
import MazeGame from './MazeGame';
import MySkills from './MySkills'
import Marketplace from './MockTradingPlatform';

import space from './images/space.jpg'
import cool from './images/neatbg.png'
import { Parallax, ParallaxLayer } from '@react-spring/parallax';

import Typewriter from "typewriter-effect";
import React, { useEffect, useRef, useState } from 'react';
import { useInView, animated } from '@react-spring/web';

import Transition from './InterPageTransition';

function App() {
  const ref = useRef();
  const [ref1, inView1] = useInView();
  const [ref2, inView2] = useInView();
  const [appear, setappear] = React.useState(false);
  const [appear2, setappear2] = React.useState(false);

  useEffect(() => {
    if (inView1) {
      setappear(true);
    }
    else {
      setappear(false);
    }
  }, [inView1]);

  useEffect(() => {
    if (inView2) {
      setappear2(true);
    }
    else {
      setappear2(false);
    }
  }, [inView2]);

  return (
    <div>
      <Parallax pages={8} ref={ref}>
        <ParallaxLayer
          offset={0}
          speed={1}
          factor={1}
          onClick={()=> ref.current.scrollTo(1)}
          style={{
            backgroundImage: `url(${space})`,
            backgroundSize: 'cover'
          }}
        ></ParallaxLayer>

        <ParallaxLayer
          offset={1}
          speed={1}
          factor={1}
          onClick={()=> ref.current.scrollTo(2)}
          style={{
            backgroundImage: `url(${cool})`,
            backgroundSize: 'cover'
          }}
        ></ParallaxLayer>

        <ParallaxLayer
          offset={0.2}
          speed={0.05}
          onClick={()=> ref.current.scrollTo(1)}
        >
          <animated.div className="App" ref={ref1}>
            {appear ? (
              <Typewriter
                onInit={(typewriter) => {
                  typewriter.typeString("Hi, I'm Nemanja!").start();
                }}
              />
            ) : null }
          </animated.div>
        </ParallaxLayer>

        <ParallaxLayer
          offset={1.2}
          speed={0.05}
          onClick={()=> ref.current.scrollTo(2)}
        >
          <animated.div className="App" ref={ref2}>
            {appear2 ? (
              <Typewriter
                onInit={(typewriter) => {
                typewriter.typeString("And this is my website!").pauseFor(2000).start();
              }}/>
            ) : null }
          </animated.div>
        </ParallaxLayer>

        <ParallaxLayer
          offset={2}
          speed={1}
          factor={1}
          onClick={()=> ref.current.scrollTo(3)}
        >
          <Projects/>
        </ParallaxLayer>

        <ParallaxLayer
          offset={2.5}
          speed={1}
          factor={1}
        >
          <Transition />
        </ParallaxLayer>

        <ParallaxLayer
          offset={3}
          speed={1}
          factor={1}
        >
          <MazeGame />
        </ParallaxLayer> 

        <ParallaxLayer
        offset={4.5}
        speed={1}
        factor={1}
        >
          <Marketplace/>
        </ParallaxLayer>

        <ParallaxLayer
        offset={5.8}
        speed={1}
        factor={1}
        >
          <MySkills/>
        </ParallaxLayer>

        <ParallaxLayer
        offset={6}
        speed={1}
        factor={1.5}
        >
          <AboutMe/>
        </ParallaxLayer>
      </Parallax>
    </div>
  );
}

export default App;
