import React, { useEffect, useRef } from 'react';
import { Parallax, ParallaxLayer } from '@react-spring/parallax';
import Typewriter from 'typewriter-effect';
import { useInView } from '@react-spring/web';
import { useSpring, animated } from '@react-spring/web';

import Projects from './Projects';
import AboutMe from './AboutMe';
import MazeGame from './MazeGame';
import MySkills from './MySkills';
import Marketplace from './MockTradingPlatform';

import space from './images/space.jpg';
import cool from './images/neatbg.png';
import Transition from './InterPageTransition';

function App() {
  const ref = useRef();
  const [ref1, inView1] = useInView();
  const [ref2, inView2] = useInView();

  const typewriter1 = useSpring({
    opacity: inView1 ? 1 : 0,
    from: { opacity: 0 },
  });

  const typewriter2 = useSpring({
    opacity: inView2 ? 1 : 0,
    from: { opacity: 0 },
  });

  useEffect(() => {
    if (inView1) {
      new Typewriter(ref1.current, {
        loop: true,
      }).typeString("Hi, I'm Nemanja!").pauseFor(2000).start();
    }
  }, [inView1]);

  useEffect(() => {
    if (inView2) {
      new Typewriter(ref2.current, {
        loop: true,
      }).typeString("And this is my website!").pauseFor(2000).start();
    }
  }, [inView2]);

  return (
    <div>
      <Parallax pages={8} ref={ref}>
        <ParallaxLayer
          offset={0}
          speed={1}
          factor={1}
          onClick={() => ref.current.scrollTo(1)}
          style={{
            backgroundImage: `url(${space})`,
            backgroundSize: 'cover',
          }}
        ></ParallaxLayer>

        <ParallaxLayer
          offset={1}
          speed={1}
          factor={1}
          onClick={() => ref.current.scrollTo(2)}
          style={{
            backgroundImage: `url(${cool})`,
            backgroundSize: 'cover',
          }}
        ></ParallaxLayer>

        <ParallaxLayer offset={0.2} speed={0.05} onClick={() => ref.current.scrollTo(1)}>
          <animated.div className="App" ref={ref1} style={typewriter1}></animated.div>
        </ParallaxLayer>

        <ParallaxLayer offset={1.2} speed={0.05} onClick={() => ref.current.scrollTo(2)}>
          <animated.div className="App" ref={ref2} style={typewriter2}></animated.div>
        </ParallaxLayer>

        <ParallaxLayer offset={2} speed={1} factor={1} onClick={() => ref.current.scrollTo(3)}>
          <Projects />
        </ParallaxLayer>

        <ParallaxLayer offset={2.5} speed={1} factor={1}>
          <Transition />
        </ParallaxLayer>

        <ParallaxLayer offset={3} speed={1} factor={1}>
          <MazeGame />
        </ParallaxLayer>

        <ParallaxLayer offset={4.5} speed={1} factor={1}>
          <Marketplace />
        </ParallaxLayer>

        <ParallaxLayer offset={5.8} speed={1} factor={1}>
          <MySkills />
        </ParallaxLayer>

        <ParallaxLayer offset={6} speed={1} factor={1.5}>
          <AboutMe />
        </ParallaxLayer>
      </Parallax>
    </div>
  );
}

export default App;
