import { Flex, Container, Box, Stack, Typography, Card, Divider, CardMedia, Grid } from "@mui/material";
import image from './images/image5.png';
import DevOps from './images/DevOps.png';
import { useSpring, animated, useScroll, useInView} from '@react-spring/web';

const commonCardStyle = {
  bgcolor: 'cyan',
  height: '300'
}

const MazeGameCard = (
    <Card sx={commonCardStyle}>
      <Typography sx={{ fontSize: 20 }} color="text.primary" gutterBottom>
        Maze Game
      </Typography>
      <Divider />
      <Box>
        <CardMedia
            component="img"
            alt="Project Image"
            src={image}
            style={{
              objectFit: "cover",
              width: "100%",
              height: "100%",
            }}
          />
      </Box>
    </Card>
);

const DevOpsCard = (
  <Card sx={commonCardStyle}>
    <Typography sx={{ fontSize: 20 }} color="text.primary" gutterBottom>
      Dev Ops
    </Typography>
    <Divider />
    <CardMedia
      component="img"
      alt="Project Image"
      src={DevOps}
      style={{
        objectFit: "cover",
        width: "100%",
        height: "100%",
      }}
    />
  </Card>
);

function Projects() {
  const [ref, inView] = useInView();

  const left2right = useSpring({
    from: { x: 0 },
    to: { x: inView ? (window.innerWidth * 0.1): 0 },
  });
  const right2left = useSpring({
    from: { x: window.innerWidth },
    to: { x: inView ? 0 : window.innerWidth },
  });
  return (
    <>
      <div>
        <animated.div
        ref={ref}
        style={{
          width: '50vw',
          height: 80,
          background: '#ff6d6d',
          borderRadius: 8,
          ...left2right,
          alignContent: 'center',
          textAlign: 'center'
        }}>
          <h1>My Projects</h1>
        </animated.div>
      </div>

      <div
        style={{}}
      >
        <animated.div
          ref={ref}
          style={{
            width: '50vw',
            height: 80,
            background: '#ff6d6d',
            borderRadius: 8,
            ...right2left,
            alignContent: 'center',
            textAlign: 'center',
          }}
        >
          {MazeGameCard}
        </animated.div>
      </div>
    </>

    /*
    <Container
      fixed
      sx={{
        bgcolor: "transparent",
        marginTop: '10vh',
        marginBottom: '10vh',
        textAlign: 'center',
      }}
    >
        <h1>Projects</h1>
        <Stack direction="row" spacing={5} sx={{ 
            justifyContent: 'flex-end',
            padding: '1vh' 
        }}>
          <Box sx={{ flex: 1, textAlign: 'center' }}>
            <a href="/MazeGame" style={{ textDecoration: "none" }}>
              {MazeGameCard}
            </a>
          </Box>
          <Box sx={{ flex: 1, textAlign: 'center'}}>
            <a href="/DevOps" style={{ textDecoration: "none" }}>
              {DevOpsCard}
            </a>
          </Box>
      </Stack>
    </Container>
    */
  );
}

export default Projects;
