import { Typography, Link, Stack } from "@mui/material";
import { useSpring, animated, useInView } from '@react-spring/web';
import React from 'react';
import image1 from './images/image1.png';
import image2 from './images/image2.png';
import image4 from './images/image4.png';
import image5 from './images/image5.png';

const itemData = [
  { img: image1, desc: 'biggest size maze as a 30x30' },
  { img: image2, desc: 'After the computer found the solution' },
  { img: image4, desc: 'the medium-sized maze at 20x20' },
  { img: image5, desc: 'the second color pallet option' },
];

function MazeImage({ imgSrc, animation, inView }) {
  return (
    <animated.div
      style={{
        width: '100%',
        height: '100%',
        margin: '10%',
        background: '#ff6d6d',
        borderRadius: 8,
        ...animation,
        alignContent: 'center',
        textAlign: 'center',
        overflow: 'hidden',
      }}
    >
      <img
        src={imgSrc}
        alt="My Projects Image"
        style={{
          width: '100%',
          height: '100%',
          objectFit: 'cover',
        }}
      />
    </animated.div>
  );
}

function MazeGame() {
  const [ref1, inView1] = useInView();
  const [ref2, inView2] = useInView();

  const show = useSpring({
    from: { opacity: 0 },
    to: { opacity: inView1 ? 1 : 0 },
    config: { duration: 2000 },
  });

  const show2 = useSpring({
    from: { opacity: 0 },
    to: { opacity: inView2 ? 1 : 0 },
    config: { duration: 2000 },
  });

  return (
    <>
      <h1>MAZE GAME</h1>
      <Typography>
        Using the SDL library with C++ I was able to design and develop a maze game. This maze game allows you to regenerate
        a new maze with just a click of a button. There are 3 maze sizes to choose from, a 12x12 a 20x20 and a 30x30 maze size.
        In addition, you can change the color palette from a blue/white layout to a green/black one. Lastly, if you get stuck you can
        click the "solve" button and the computer will show you how to complete the maze! If you want to look at how the program
        looks like you can check out the screenshots and gifs below.
      </Typography>
      <Link href="https://github.com/TheRealJasoandRazi/mewed" target="_blank" rel="noopener noreferrer">
        Source Code Here
      </Link>
      <Stack spacing={2} direction="row" style={{ width: '100%', height: '100%' }}>
        {itemData.map((item, index) => (
          <Stack spacing={10} key={index}>
            <MazeImage imgSrc={item.img} animation={show} inView={inView1} />
            <MazeImage imgSrc={item.img} animation={show2} inView={inView2} />
          </Stack>
        ))}
      </Stack>
    </>
  );
}

export default MazeGame;
