import { Flex, Container, Box, Stack, Typography, Card, Divider, CardMedia, Grid } from "@mui/material";
import image from './images/image5.png';
import TradingPlatform from './images/MockTradingPlatform.jpeg';
import { useSpring, animated, useScroll, useInView} from '@react-spring/web';
import React from 'react';

const commonCardStyle = {
  bgcolor: 'cyan',
  height: '300'
}

const MazeGameCard = (
    <Card sx={commonCardStyle}>
      <Typography sx={{ fontSize: 20 }} color="text.primary" gutterBottom>
        Maze Game
      </Typography>
      <Divider />
      <Box>
        <CardMedia
            component="img"
            alt="Project Image"
            src={image}
            style={{
              objectFit: "cover",
              width: "100%",
              height: "100%",
            }}
          />
      </Box>
    </Card>
);

const MockTradingPlatform = (
  <Card sx={commonCardStyle}>
    <Typography sx={{ fontSize: 20 }} color="text.primary" gutterBottom>
      Mock Trading Platform
    </Typography>
    <Divider />
    <CardMedia
      component="img"
      alt="Project Image"
      src={TradingPlatform}
      style={{
        objectFit: "cover",
        width: "100%",
        height: "100%",
      }}
    />
  </Card>
);

function Projects() {
  const getWindowWidth = () => { //used for jest test since it doesn't open browsers
    if (typeof window !== 'undefined') {
        return window.innerWidth;
    }
    return 0;
  };

    const [ref1, inView1] = useInView();
    const [ref2, inView2] = useInView();
    const [ref3, inView3] = useInView();

    const left2right = useSpring({
      from: { x: 0 },
      to: { x: inView1 ? getWindowWidth() * 0.1 : 0 },
    });
    const right2left = useSpring({
      from: { x: getWindowWidth() },
      to: { x: inView2 ? getWindowWidth() * 0.4 : getWindowWidth() },
    });
    const left2right2 = useSpring({
      from: { x: 0 },
      to: { x: inView3 ? getWindowWidth() * 0.1 : 0 },
    });
    return (
    <Stack spacing={2} sx={{paddingTop: '1%'}}>
            <animated.div
                ref={ref1}
                style={{
                    width: '50%',
                    height: '33%',
                    background: '#ff6d6d',
                    borderRadius: 8,
                    ...left2right,
                    alignContent: 'center',
                    textAlign: 'center'
                }}
            >
                <h1>My Projects</h1>
            </animated.div>

            <animated.div
                ref={ref2}
                style={{
                    width: '50%',
                    height: '33%',
                    background: '#ff6d6d',
                    borderRadius: 8,
                    ...right2left,
                    alignContent: 'center',
                    textAlign: 'center',
                }}
            >
                {MazeGameCard}
            </animated.div>

            <animated.div
                ref={ref3}
                style={{
                    width: '50%',
                    height: '33%',
                    background: '#ff6d6d',
                    borderRadius: 8,
                    ...left2right2,
                    alignContent: 'center',
                    textAlign: 'center',
                }}
            >
                {MockTradingPlatform}
            </animated.div>
    </Stack>
  );
}

export default Projects;
