import { AppBar, Divider, Icon, Toolbar } from '@mui/material';
import * as React from 'react';
import {
    Stack,
    IconButton,
    Box,
    TextField,
    ThemeProvider,
    MenuItem,
    Menu,
    Typography,
    Container,
    Popper,
    Paper,
    Fade,
    Grow,
    CardMedia,
    Link,
    Grid,
  } from "@mui/material";
import { animated, useInView, useSpring } from '@react-spring/web'

import List from "./List";
import cart from './images/cart.png'
import asset from './images/Asset.png'
import marketplace from './images/marketplace.png'
import transactions from './images/transactions.png'
import marketgif from './videos/Marketplace.gif'
import assetgif from './videos/Asset.gif'
import logingif from './videos/Login.gif'

const itemData = [
    {
      img: marketplace,
      desc: 'shows the page with that allows assets to be searched',
    },
    {
      img: asset,
      desc: 'the page that shows individual assets information',
    },
    {
      img: cart,
      desc: 'the cart feature where the user buys all of their selected assets',
    },
    {
      img: transactions,
      desc: 'the list of transactions made by the user',
    }];

const videoData = [
        {
          img: marketgif,
          desc: 'shows the live search feature in the marketplace',
        },
        {
            img: assetgif,
            desc: 'shows the process of buying assets with the cart',
        },
        {
          img: logingif,
          desc: 'the login feature',
        }
    ];

    function CustomTabPanel(props) {
      const { children, value, index, ...other } = props;

      return (
        <div
          role="tabpanel"
          hidden={value !== index}
          id={`simple-tabpanel-${index}`}
          aria-labelledby={`simple-tab-${index}`}
          {...other}
        >
          {value === index && (
            <Box sx={{ p: 3 }}>
              <Typography>{children}</Typography>
            </Box>
          )}
        </div>
      );
    }

    CustomTabPanel.propTypes = {
      children: PropTypes.node,
      index: PropTypes.number.isRequired,
      value: PropTypes.number.isRequired,
    };

    function a11yProps(index) {
      return {
        id: `simple-tab-${index}`,
        'aria-controls': `simple-tabpanel-${index}`,
      };
    }
function Platform() {
  const [screenshotTab, setScreenshotTab] = React.useState(0);
  const [gifsTab, setGifsTab] = React.useState(0);
  const [showGifs, setShowGifs] = React.useState(false);

  const handleScreenshotChange = (event, newValue) => {
    setScreenshotTab(newValue);
  };

  const handleGifsChange = (event, newValue) => {
    setGifsTab(newValue);
  };

  const handleToggleGifs = () => {
    setShowGifs(!showGifs);
    setGifsTab(0);
  };
    return (
        <>
            <div style={{ textAlign: 'center' }}>
                <h1>My Mock Trading Platform</h1>
                <Typography>
                    As apart of my course i was froming a team of other students in order to create a fully
                    working trading platform with backend and frontend components. The primary features of this project is to display the 
                    digital assets that other users have uploaded in the digital marketplace where it can be added to a "cart"
                    and bought. Another feature of the website is that it has a live search feature for when searching through the marketplace 
                    and any assets bought will be stored in the "transaction history".
                </Typography>
                <Typography>
                    The projects utilises React.js and Material UI for the frontend.
                    Express.js for developing the API.
                    Truffle and Ganache for creating smart contracts and the blockchain.
                    MariaDB to store meta data for the Assets.
                </Typography>
                <Link href="https://github.com/TheRealJasoandRazi/innoproj" target="_blank" rel="noopener noreferrer">
                    Source Code Here
                </Link>
                <h3>ScreenShots</h3>
            </div>

            <Box sx={{ width: '100%' }}>
              <Box sx={{ borderBottom: 1, borderColor: 'divider' }}>
                <Tabs value={screenshotTab} onChange={handleScreenshotChange} aria-label="basic tabs example" centered>
                  {itemData.map((item, index) => (
                    <Tab key={index} label={`Screenshot ${index+1}`} {...a11yProps(index)} sx={{color: 'white'}} />
                  ))}
                </Tabs>
              </Box>

            {itemData.map((item, index) => (
              <CustomTabPanel key={index} value={screenshotTab} index={index}>
                <Grid container spacing={2}>
                  <Grid item xs={12} md={6}>
                    <img
                      src={item.img}
                      alt={`Item ${index + 1}`}
                      style={{
                        width: '100%',
                        height: '100%',
                        objectFit: 'fill',
                      }}
                      loading="lazy"
                    />
                  </Grid>
                  <Grid item xs={12} md={6} sx={{ display: 'flex', alignItems: 'center' }}>
                    <Typography
                      sx={{textAlign: 'center'}}
                    >
                      {item.desc}</Typography>
                  </Grid>
                </Grid>
              </CustomTabPanel>
            ))}
            </Box>

            <Button variant="contained" onClick={handleToggleGifs}
            sx={{ mx: 'auto', display: 'block' }}
            >
              {showGifs ? 'Hide Gifs' : 'Show GIFs'}
            </Button>

            {showGifs ? (
              <>
              <h3 style={{ textAlign: 'center' }}>Gifs</h3>
              <Box sx={{ width: '100%' }}>
                <Box sx={{ borderBottom: 1, borderColor: 'divider' }}>
                  <Tabs value={gifsTab} onChange={handleGifsChange} aria-label="basic tabs example" centered>
                    {GifsData.map((item, index) => (
                      <Tab key={index} label={`Gif ${index+1}`} {...a11yProps(index)} sx={{color: 'white'}} />
                    ))}
                  </Tabs>
                </Box>

              {GifsData.map((item, index) => (
                <CustomTabPanel key={index} value={gifsTab} index={index}>
                  <Grid container spacing={2}>
                    <Grid item xs={12} md={6}>
                      <img
                        src={item.img}
                        alt={`Item ${index + 1}`}
                        style={{
                          width: '100%',
                          height: '100%',
                          objectFit: 'fill',
                        }}
                        loading="lazy"
                      />
                    </Grid>
                    <Grid item xs={12} md={6} sx={{ display: 'flex', alignItems: 'center' }}>
                      <Typography
                        sx={{textAlign: 'center'}}
                      >
                        {item.desc}</Typography>
                    </Grid>
                  </Grid>
                </CustomTabPanel>
              ))}
              </Box>
              </>
              ) : null}
        </>
    );
};
export default Platform;
